func TestCopyMetaDataSlice(t *testing.T) {
	src := []MetaData{}
	dest := []MetaData{}
	// Test CopyTo empty
	dest = CopyMetaDataSlice(dest, src)
	assert.Equal(t, []MetaData{}, dest)

	// Test CopyTo larger slice
	src = GenTestMetaDataSlice()
	dest = CopyMetaDataSlice(dest, src)
	assert.Equal(t, GenTestMetaDataSlice(), dest)

	// Test CopyTo same size slice
	dest = CopyMetaDataSlice(dest, src)
	assert.Equal(t, GenTestMetaDataSlice(), dest)

	// Test CopyTo smaller size slice
	dest = CopyMetaDataSlice(dest, []MetaData{})
	assert.Len(t, dest, 0)

	// Test CopyTo larger slice with enough capacity
	dest = CopyMetaDataSlice(dest, src)
	assert.Equal(t, GenTestMetaDataSlice(), dest)
}

